import { useState, useEffect } from "react";

function Game(){
    const [score, setScore] = useState(0);

    const handleUserInput = (e) => {
        const re = /^[0-9]+$/;

        if (e.target.value === '' || re.test(e.target.value)) {
            setScore(score+1)
            console.log(score)
            console.log(e.target.value)
        }
    };

    return <input onKeyUp={handleUserInput}></input>;
}

export default Game;
