import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import classes from "./Game.module.css";
import Graph from "../components/Graph";

function getRandomInt(min, max) {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min) + min); //The maximum is exclusive and the minimum is inclusive
}

function Game() {
  const [score, setScore] = useState(100);
  const [kellyScore, setKellyScore] = useState(100);
  const [text, setText] = useState("");
  const [questionType, setQuestionType] = useState(getRandomInt(0, 3));
  const [questionBounds, setQuestionBounds] = useState(() => {
    if (questionType === 0) {
      return [getRandomInt(1, 51), getRandomInt(51, 101)];
    } else if (questionType === 1) {
      return getRandomInt(1, 2);
    } else {
      return getRandomInt(1, 3);
    }
  });
  const [answer, setAnswer] = useState(69);
  const { difficulty, time } = useParams();
  const [round, setRound] = useState(1);
  const [timer, setTimer] = useState(time);
  const [scoreHistory, setScoreHistory] = useState([[0, 100]]);
  const [kellyScoreHistory, setKellyScoreHistory] = useState([[0, 100]]);

  let roundInterval;
  useEffect(() => {
    roundInterval = setInterval(() => {
      if (timer > 0) {
        setTimer(timer - 1);
      }
      if (timer === 0) {
        handleRound();
      }
    }, 1000);
    return () => {
      clearInterval(roundInterval);
    };
  }, [timer]);

  useEffect(() => {
    // setQuestionType(getRandomInt(0, 3), () => {
    //   setQuestionBounds(() => {
    //     if (questionType === 0) {
    //       return [getRandomInt(1, 51), getRandomInt(51, 101)];
    //     } else if (questionType === 1) {
    //       return getRandomInt(1, 2);
    //     } else {
    //       return getRandomInt(1, 3);
    //     }
    //   });
    // });
  }, [round]);

  const handleRound = () => {
    if (round <= Math.floor(300 / time)) {
      setTimer(time);
      setScoreHistory([...scoreHistory, [round, score]]);
      setKellyScoreHistory([...kellyScoreHistory, [round, kellyScore]]);
      setRound(round + 1);
      setText("");
      console.log("next round");
    }
  };

  const ensureValidInput = (e) => {
    const re = /^[0-9]+$/;

    if (e.target.value === "" || re.test(e.target.value)) {
      setText(e.target.value);
    }
  };

  const checkUserInput = (e) => {
    if (e.key === "Enter") {
      handleRound();
    }
  };

  return (
    <div>
      {(() => {
        if (round <= Math.floor(300 / time)) {
          return (
            <div className={classes.remaining}>
              <div className={classes.round}>Round: {round}</div>
              <div className={classes.timer}>Time: {timer}</div>
            </div>
          );
        }
        return (
          <div className={classes.remaining}>
            <div>Game Over</div>
          </div>
        );
      })()}
      <div className={classes.question}>
        <div className={classes.questionType}>
          {(() => {
            if (questionType === 0) {
              return (
                "Uniform Random Variable between " +
                questionBounds[0] +
                " and " +
                questionBounds[1]
              );
            } else if (questionType === 1) {
              return (
                "Rolling " +
                questionBounds +
                (questionBounds === 1 ? " die" : " dice")
              );
            } else {
              return (
                "Flipping " +
                questionBounds +
                (questionBounds === 1 ? " quarter" : " quarters")
              );
            }
          })()}
        </div>
        <div className={classes.odds}>W/L Odds: 20%, 100%</div>
        <div className={classes.questionText}>This is the question</div>
      </div>
      <div className={classes.terminal}>
        <div className={classes.score}>{score}</div>
        <input
          value={text}
          onKeyDown={checkUserInput}
          onChange={ensureValidInput}
          placeholder="Bet amount"
        />
        <div className={classes.score}>{kellyScore}</div>
      </div>

      <div className={classes.results}>
        <Graph
          key={round}
          history={scoreHistory}
          kellyHistory={kellyScoreHistory}
        />
      </div>
    </div>
  );
}

export default Game;
